// SPDX-License-Identifier: MIT
pragma solidity ^0.8.16;

import "@scroll-tech/contracts/libraries/IScrollMessenger.sol";

contract CrossChainCrowdfunding {
    address public projectOwner;
    uint256 public fundingGoal;
    uint256 public deadline;
    uint256 public totalFunds;
    mapping(address => uint256) public pledges;
    bool public goalReached;
    bool public fundsWithdrawn;

    address public scrollMessenger;
    address public l1CrowdfundingContract;

    event Pledged(address indexed backer, uint256 amount);
    event GoalReached(uint256 totalFunds);
    event FundsBridgedToL1(uint256 amount);
    event Refunded(address indexed backer, uint256 amount);

    modifier onlyOwner() {
        require(msg.sender == projectOwner, "Not the project owner");
        _;
    }

    modifier beforeDeadline() {
        require(block.timestamp < deadline, "Deadline has passed");
        _;
    }

    constructor(
        uint256 _goal,
        uint256 _duration,
        address _scrollMessenger,
        address _l1CrowdfundingContract
    ) {
        projectOwner = msg.sender;
        fundingGoal = _goal;
        deadline = block.timestamp + _duration;
        scrollMessenger = _scrollMessenger;
        l1CrowdfundingContract = _l1CrowdfundingContract;
    }

    function pledge() public payable beforeDeadline {
        require(msg.value > 0, "Must send ETH");
        pledges[msg.sender] += msg.value;
        totalFunds += msg.value;

        emit Pledged(msg.sender, msg.value);

        if (totalFunds >= fundingGoal && !goalReached) {
            goalReached = true;
            emit GoalReached(totalFunds);
        }
    }

    function bridgeFundsToL1(uint32 gasLimit) public onlyOwner {
        require(goalReached, "Funding goal not reached");
        require(!fundsWithdrawn, "Funds already withdrawn");
        fundsWithdrawn = true;

        IScrollMessenger(scrollMessenger).sendMessage{value: totalFunds}(
            l1CrowdfundingContract,
            0,
            abi.encodeWithSignature("receiveBridgedFunds()"),
            gasLimit,
            msg.sender
        );

        emit FundsBridgedToL1(totalFunds);
    }

    function refund() public {
        require(block.timestamp > deadline, "Funding period not over");
        require(!goalReached, "Funding goal reached");

        uint256 amount = pledges[msg.sender];
        require(amount > 0, "No funds to refund");

        pledges[msg.sender] = 0;
        payable(msg.sender).transfer(amount);

        emit Refunded(msg.sender, amount);
    }

    receive() external payable {}
}

